mapper.select:
SELECT

FROM product_spu


        id > #{id}

    AND deleted = 0

ORDER BY id ASC
LIMIT #{limit}

mapper.insert:
INSERT INTO product_spu (
    name, sell_point, description, cid, pic_urls,
    visible, sort, price, quantity,
    deleted, create_time
) VALUES (
    #{name}, #{sellPoint}, #{description}, #{cid}, #{picUrls},
    #{visible}, #{sort}, #{price}, #{quantity},
     #{deleted}, #{createTime}
)

mapper.select:
SELECT

FROM product_spu


        name LIKE "%"#{name}"%"


        AND cid = #{cid}


        AND visible = #{visible}


        AND quantity > 0


        AND quantity = 0

    AND deleted = 0

ORDER BY sort ASC

    LIMIT #{offset}, #{limit}
