mapper.select:
SELECT

FROM resource
WHERE type = #{type}
AND handler = #{handler}
AND deleted = 0
LIMIT 1

SELECT r.id, rr.resource_id, rr.role_id, r.name, r.type, r.sort, r.display_name, r.create_time, r.pid, r.handler, r.deleted, rr.deleted FROM resource r, role_resource rr WHERE r.deleted = 0 AND rr.deleted = 0
AND r.type = #{type}
AND rr.role_id IN
#{roleId}
AND r.id = rr.resource_id
